import React, { useEffect, useState } from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserID.js";


export const Home = () =>{
    const [recipes, setRecipes] = useState([]);
    const userID = useGetUserID();
    useEffect(() => {

        const fetchRecipe = async() => {
        try{
            const response = await axios.get("http://localhost:3001/recipes/ids/${userID}");
            setRecipes(response.data.savedRecipes);
            alert("Recipe Created");
            console.log(response.data);
        } catch (err) {
            console.error(err);
        }
        };



    fetchRecipe();

    }, []);

    const saveRecipe = async (recipeID) => {
    try{

        const response = await axios.put("http://localhost:3001/recipes", {
            recipeID,
            userID,
        });
        setRecipes(response.data.savedRecipes);
    }   catch (err) {
        console.error(err);
    }
};


    return <div>
        {" "} <h1>Recipes</h1> 
        <ul>
            {recipes.map((recipe) => (
                <li key = {recipe._id}>
                    <div>
                      <h2>{recipe.name}</h2>  
                      <button onClick={() => saveRecipe(recipe._id)}> Save Recipe </button>
                    </div>
                    <div className="instructions">
                    <p>{recipe.instructions}</p>
                    <img src = {recipe.imageUrl} alt = {recipe.name}></img>
                    <p> Cooking Time: {recipe.cookingTime} minutes </p>
                    </div>
                </li>
            ) )}</ul></div>;
}
